const viewport = document.getElementById("viewport"),
    zoom = document.getElementById("zoom"),
    engine = new GlancerEngine.Glancer(viewport, { alpha: true, antialias: true });

Promise.all([
	engine.resources.getModel(viewport.getAttribute("file")),
	engine.resources.getResources(viewport.getAttribute("mat")),
	engine.resources.loadShadersXML("shaders.xml")
]).then(([
    model
]) => {
    let scene = new GlancerEngine.ShowRoomScene(model);
    range = scene.camera.position[2];
    engine.start(scene);
    zoom.oninput = event => scene.camera.position[2] = range * event.target.value;
});